def-group:

  {{{ oval_metadata("The password hashing algorithm should be set correctly in /etc/pam.d/system-auth.") }}}





{{% if product in ['sle12', 'sle15'] %}}
  {{% set pam_file = "/etc/pam.d/common-password" %}}
  {{% set hashing_pattern = "^[\s]*password[\s]+(?:(?:required))[\s]+pam_unix\.so[\s]+.*sha512.*$" %}}
{{% elif 'ubuntu' in product %}}
  {{% set pam_file = "/etc/pam.d/common-password" %}}
  {{% set hashing_pattern = "^[\s]*password[\s]+(?:\[success=\d+\s+default=ignore\])[\s]+pam_unix\.so[\s]+[^#]*[\s]sha512(\s.*)?$" %}}
{{% else %}}
  {{% set pam_file = "/etc/pam.d/system-auth" %}}
  {{% set hashing_pattern = "^[\s]*password[\s]+(?:(?:required)|(?:sufficient))[\s]+pam_unix\.so[\s]+.*sha512.*$" %}}
{{% endif %}}






  {{{ pam_file }}}
  {{{ hashing_pattern }}}
  1
